import React from 'react';
import { Mail, TrendingDown } from 'lucide-react';

export const ProgressCounter = ({ totalEmails, processedEmails, currentArchetype, onOpenBottomSheet }) => {
  const remaining = totalEmails - processedEmails;
  const progressPercent = totalEmails > 0 ? (processedEmails / totalEmails) * 100 : 0;

  return (
    <button 
      onClick={onOpenBottomSheet}
      className="fixed top-4 left-1/2 transform -translate-x-1/2 z-40 bg-slate-900/90 backdrop-blur-xl hover:bg-slate-800/90 text-white px-6 py-3 rounded-2xl shadow-2xl border border-slate-700 transition-all"
    >
      <div className="flex items-center gap-3">
        <Mail size={16} className="text-blue-400" />
        <div className="flex items-center gap-2">
          <span className="text-2xl font-bold">{remaining}</span>
          <TrendingDown size={16} className="text-green-400" />
          <span className="text-sm text-slate-400">emails left</span>
        </div>
        <div className="w-16 h-2 bg-slate-700 rounded-full overflow-hidden ml-2">
          <div 
            className="h-full bg-gradient-to-r from-blue-500 to-green-500 transition-all duration-500"
            style={{ width: `${progressPercent}%` }}
          />
        </div>
      </div>
    </button>
  );
};
